import firebase from './Firebase/firebase.jsx';
import React,{Component} from 'react';
import ReactDataGrid from 'react-data-grid';

var database = firebase.database();
/** 
 *  @author agoethel
 *  10/25 - file created agoethel
 * 
 */
const columns = [
    { key: 'id', name: 'ID' },
    { key: 'title', name: 'Title' },
    { key: 'count', name: 'Count' } ];

  const rows = [{id: 0, title: 'row1', count: 20}, {id: 1, title: 'row1', count: 40}, {id: 2, title: 'row1', count: 60}];

  function HelloWorld() {

  }

  export default class PlayerDatabase extends Component {
    constructor(props) {
        super(props);
    }
    /** This function will query the database for a particular player name
     * 
     * @param {string} name 
     */
    getPlayer(name) {
        var mostViewedPosts = firebase.database().ref('Players').orderByChild('NAME').equalTo("Lionel Messi");
    }
    render() {
        return (<ReactDataGrid
            columns={columns}
            rowGetter={i => rows[i]}
            rowsCount={3}
            minHeight={150} />);

    }
}
